	}
);

function appendInterface(main, resource) {

	return parseInterface.aceUtils.overlayEntity(
		main,
		resource // STUB
	);
}

function executeModule(utility) {

	executeModule.interface =
		executeModule.interface != null && !executeModule.refresh ?
			executeModule.interface : getInterface();

	executeModule.refresh = false;

	let interface = executeModule.interface;

	if(utility == null)
		return interface;

	let components = {
		type: { library: { } },
		environment: { }
	};

	components.environment[
		typeof utility == "string" ?
			"javascript" : "kaeon fusion"
	] = { };

	let utilities = parseInterface.aceUtils.traceKaeonACE(
		interface,
		[
			...(
				typeof utility == "string" ?
					(utility.includes(".") ?
						utility.split(".").slice(
							0,
							utility.split(".").length - 1
						) :
						[]
					).map(item => { return { alias: item } }) :
					[]
			),
			{
				alias: typeof utility == "string" ?
					(utility.includes(".") ?
						utility.substring(utility.lastIndexOf(".") + 1) :
						utility
					) :
					null,
				components: components,
				filter: (item) => {

					return item.components.locations != null ||
						item.components.source != null;
				}
			}
		]
	).map(item => {
		return use(Object.keys(item.components.locations)[0]);
	});

	if(typeof utility != "string") {

		utilities.forEach((item) => {

			try {
				item(utility);
			}

			catch(error) {

			}
		});
	}

	return utilities[0];
}

function isOverridden(entity, path) {

	try {

		if(typeof entity != "object")
			return false

		path = path != null ? path : [];

		let flag = false;

		if(path.length >= 2) {

			flag =
				path[path.length - 2].toLowerCase() == "components" &&
				path[path.length - 1].toLowerCase() == "flag";
		}

		let keys = Object.keys(entity);

		for(let i = 0; i < keys.length; i++) {

			if(flag && keys[i].toLowerCase() == "override")
				return true;

			if(isOverridden(entity[keys[i]], path.concat(keys[i])))
				return true;
		}

		return false;
	}

	catch(error) {
		return false;
	}
}

function parseInterface(interface) {

	if(parseInterface.aceUtils == null) {

		eval(
			moduleDependencies.preload.map((item) => {

				return openResource(item.link).
					split("\n").
					filter((line) => {
						return !line.includes("kaeon-united");
					}).
					join("\n").
					split("\nmodule.exports").
					join("\nvar " + item.name).
					split("moduleDependencies").
					join(item.name + "Dependencies").
					split("module.exports").
					join(item.name);
				}
			).join("\n")
		);

		parseInterface.aceUtils = aceUtils;
	}

	return parseInterface.aceUtils.useACE(
		JSON.stringify(
			parseInterface.aceUtils.formatKaeonACE(
				interface.main != null ? interface.main : interface,
				interface.reference
			)
		)
	);
}

var one = use("kaeon-united")("one");
var onePlus = use("kaeon-united")("onePlus");

var kaeonFUSION = use("kaeon-united")("kaeonFUSION");

var universalPreprocessor = use("kaeon-united")("universalPreprocessor");

function preprocess(string, args) {
	return universalPreprocessor.preprocess(string, args);
}

function process(code, fusion) {

	code =
		Array.isArray(code) ?
			one.toObject(code) :
			read("" + code);

	if(fusion == null) {

		start = true;

		fusion = { fusion: new kaeonFUSION.KaeonFUSION() };
	}

	else if(fusion.fusion == null) {

		start = true;

		fusion.fusion = new kaeonFUSION.KaeonFUSION();
	}

	fusion.fusion.internalProcess(code);

	return fusion.fusion.returnValue;
}

function read(string) {

	return onePlus.read(
		preprocess(
			string.split("\r").join("")
		)
	);
}

function write(element) {

	return Array.isArray(element) ?
		one.write(one.toObject(element)) :
		one.write(element);
}

module.exports = {
	preprocess,
	process,
	read,
	write
}

function getPlatform() {

	if(typeof process === 'object') {

		if(typeof process.versions === 'object') {

			if(typeof process.versions.node !== 'undefined') {
				return "node";
			}
		}
	}

	return "browser";
}

module.exports = {
	getPlatform
};

var moduleDependencies = {
	cors: {
		utils: {
			formatGithubURI: (uri) => {

				uri = uri.substring(34);
				let user = uri.substring(0, uri.indexOf("/"));

				uri = uri.substring(uri.indexOf("/") + 1);
				let repo = uri.substring(0, uri.indexOf("/"));

				uri = uri.substring(uri.indexOf("/") + 1);

				return "https://cdn.jsdelivr.net/gh/" +
					user +
					"/" +
					repo +
					"/" +
					uri.substring(uri.indexOf("/") + 1);
			}
		},
		proxies: {
			"https://corsproxy.io/": (request) => {

				request = JSON.parse(JSON.stringify(request));

				if(request.request.uri.startsWith(
					"https://raw.githubusercontent.com/")) {

					request.request.uri =
						moduleDependencies.cors.utils.formatGithubURI(
							request.request.uri
						);
				}

				else {

					request.request.uri =
						"https://corsproxy.io/?url=" +
							encodeURIComponent(
								request.request.uri
							).split("%20").join("%2520");
				}

				return request;
			},
			"https://nextjs-cors-anywhere.vercel.app/": (request) => {

				request = JSON.parse(JSON.stringify(request));

				if(request.request.uri.startsWith(
					"https://raw.githubusercontent.com/")) {

					request.request.uri =
						moduleDependencies.cors.utils.formatGithubURI(
							request.request.uri
						);
				}

				else {

					request.request.uri =
						"https://nextjs-cors-anywhere.vercel.app/api?endpoint=" +
							request.request.uri;
				}

				return request;
			}
		},
		proxy: "https://corsproxy.io/"
	}
};

var platform = use("kaeon-united")("platform");

function getURLArguments(url) {

	if(url == null)
		url = window.location.href;

	let vars = {};

	url.replace(
		/[?&]+([^=&]+)=([^&]*)/gi,
		function(m, key, value) {
			vars[decodeURIComponent(key)] = decodeURIComponent(value);
		}
	);

	return vars;
}

function toHTTP(json) {

	let http = "";

	if(json.request != null) {

		http += json.request.method + " ";
		http += json.request.uri;

		if(json.request.version != null)
			http += " " + json.request.version;
	}

	else {

		if(json.response.version != null)
			http += " " + json.response.version;

		if(json.response.status != null)
			http += " " + json.response.status;

		if(json.response.reason != null)
			http += " " + json.response.reason;
	}

	if(json.headers != null) {

		let keys = Object.keys(json.headers);

		for(let i = 0; i < keys.length; i++)
			http += "\n" + keys[i] + ": " + json.headers[keys[i]];
	}

	if(json.body != null)
		http += "\n\n" + json.body;

	return http;
}

function toJSON(http) {

	let json = { };

	let lines = http.split("\n");
	let definition = lines[0].split(" ");

	if(!definition[0].includes("/")) {

		json.request = {
			method: definition[0],
			uri: definition[1]
		};

		if(definition.length >= 3)
			json.request.version = definition[2];
	}

	else {

		json.response = {
			version: definition[0],
			status: definition[1]
		};

		if(definition.length >= 3) {

			json.response.reason = lines[0].substring(
				definition[0].length + definition[1].length + 2
			);
		}
	}

	for(let i = 1; i < lines.length; i++) {

		if(lines[i].trim() != "") {

			if(json.headers == null)
				json.headers = { };

			let alias = lines[i].substring(0, lines[i].indexOf(":")).trim();
			let value = lines[i].substring(lines[i].indexOf(":") + 1).trim();

			json.headers[alias] = value;
		}

		else {

			json.body = lines.slice(i + 1).join("\n");

			break;
		}
	}

	return json;
}

function sendRequest(request, callback, cors) {

	let getValue = (object, key) => {

		return object[Object.keys(object).filter(
			item => item.toLowerCase().trim() == key.toLowerCase().trim()
		)[0]];
	}

	cors = cors != false ?
		(typeof cors == "function" ? cors : module.exports.cors) : null;

	if(typeof request == "string")
		request = toJSON(request);

	let call = null;
	let cacheItem = { content: "", properties: { reload: true } };

	if(platform.getPlatform() == "node") {

		try {

			if(sendRequest.cache == null) {

				let fs = use("fs");

				if(fs.existsSync("./localCache.json")) {

					sendRequest.cache = JSON.parse(
						fs.readFileSync("./localCache.json", "utf-8")
					);
				}

				else
					sendRequest.cache = { cache: [], reference: { } };
			}

			let uri = request.request.uri;

			if(getValue(sendRequest.cache.reference, uri) != null) {

				cacheItem = sendRequest.cache.cache[
					getValue(sendRequest.cache.reference, uri)
				];

				let reload = cacheItem.properties != null ?
					(cacheItem.properties.reload != null ?
						cacheItem.properties.reload : true) :
					true;

				if(!reload) {

					let response = {
						response: { version: "", status: "200" },
						headers: { },
						body: cacheItem.content.split("\r").join("")
					};

					if(callback != null)
						callback(response);

					return response;
				}
			}

			else {

				sendRequest.cache.reference[uri] =
					sendRequest.cache.cache.length;

				sendRequest.cache.cache.push(cacheItem);
			}
		}

		catch(error) {

		}

		call = new (use("xmlhttprequest").XMLHttpRequest)();
	}

	else {

		call = new XMLHttpRequest();

		if(cors != null && !(
			request.request.uri.startsWith("http://localhost") ||
			request.request.uri.startsWith("https://localhost") ||
			request.request.uri.startsWith("http://127.0.0.1") ||
			request.request.uri.startsWith("https://127.0.0.1"))) {

			request = cors(request);

			if(request.headers == null)
				request.headers = { };
		}
	}

	call.open(request.request.method, request.request.uri, callback != null);

	if(request.headers != null) {

		let keys = Object.keys(request.headers);

		for(let i = 0; i < keys.length; i++)
			call.setRequestHeader(keys[i], request.headers[keys[i]]);
	}

	var response = {
		response: { version: "", status: "" },
		headers: { },
		body: ""
	};

	call.onreadystatechange = function() {

		if(call.readyState === 4) {

			response.response.status = call.status;

			let headers = call.getAllResponseHeaders().trim();

			if(headers != null) {

				headers = headers.split("\r\n");

				for(let i = 0; i < headers.length; i++) {

					let header = headers[i].split(":");

					if(header.length >= 2)
						response.headers[header[0].trim()] = header[1].trim();

					else
						response.headers[header[0].trim()] = "";
				}
			}

			response.body = call.responseText;

			if(platform.getPlatform() == "node") {

				cacheItem.content = response.body;

				try {

					use("fs").writeFileSync(
						"./localCache.json",
						JSON.stringify(sendRequest.cache)
					);
				}

				catch(error) {

				}
			}

			if(callback != null)
				callback(response);
		}
	}

	call.send(request.body);

	if(callback == null)
		return response;
}

module.exports = {
	cors: moduleDependencies.cors.proxies[moduleDependencies.cors.proxy],
	corsOptions: moduleDependencies.cors,
	getURLArguments,
	toHTTP,
	toJSON,
	sendRequest
};

var httpUtils = use("kaeon-united")("httpUtils");
var platform = use("kaeon-united")("platform").getPlatform();

module.exports = {
	cors: httpUtils.cors,
	getInput: platform == "node" ?
		(query) => {

			return use('readline-sync').question(
				query != null ? query : ""
			);
		} :
		(query) => {
			return prompt("" + (query != null ? query : ""));
		},
	open: (file, callback, cors) => {

		if(typeof file == "function") {

			let input = document.createElement("input");

			input.setAttribute("type", "file");
			input.setAttribute("style", "display: none");

			let listener = function(event) {

				let upload = event.target.files[0];

				if(!upload)
					return;

				let reader = new FileReader();

				reader.onload = function(event) {
					file(event.target.result, upload.name);
				};

				reader.readAsText(upload);
			}

			input.addEventListener(
				'change',
				listener,
				false
			);

			document.documentElement.appendChild(input);

			input.click();

			document.documentElement.removeChild(input);

			return;
		}

		if(platform != "node" ||
			file.toLowerCase().startsWith("http://") ||
			file.toLowerCase().startsWith("https://")) {

			let response = httpUtils.sendRequest(
				{
					request: {
						method: "GET",
						uri: file
					}
				},
				callback != null ? (response) => {
					callback(response.body);
				} : null,
				cors != false ?
					(typeof cors == "function" ?
						cors : module.exports.cors
					) : false
			);

			if(callback == null)
				return response.body;
		}

		else if(callback == null)
			return use("fs").readFileSync(file, "utf-8");

		else {

			use("fs").readFile(file, null, (error, data) => {

				if(error != null)
					callback("");

				callback(data);
			});
		}
	},
	save: platform == "node" ?
		(content, file) => {
			use('fs').writeFileSync(file, content);
		} :
		(content, file) => {

			let element = document.createElement('a');

			element.setAttribute(
				'href',
				'data:text/plain;charset=utf-8,' +
					encodeURIComponent(content));

			element.setAttribute('download', file);

			element.style.display = 'none';
			document.documentElement.appendChild(element);

			element.click();

			document.documentElement.removeChild(element);
		}
};

function tokenize(tokens, string) {

	var tokenize = [];

	var undefined = "";

	for(var i = 0; i < string.length; i++) {

		var token = getToken(tokens, string, i);

		if(token == null)
			undefined += string.charAt(i);

		else {

			if(undefined.length > 0) {

				tokenize.push(undefined);

				undefined = "";
			}

			tokenize.push(token);

			i += token.length - 1;
		}
	}

	if(undefined.length > 0)
		tokenize.push(undefined);

	return tokenize;
}

function getToken(tokens, string, index) {

	var validTokens = [];

	for(var i = 0; i < tokens.length; i++) {

		var token = tokens[i];

		if(isToken(token, string, index))
			validTokens.push(token);
	}

	if(validTokens.length == 0)
		return null;

	var validToken = validTokens[0];

	for(var i = 1; i < validTokens.length; i++) {

		if(validTokens[i].length > validToken.length)
			validToken = validTokens[i];
	}

	return validToken;
}

function isToken(token, string, index) {

	if(string.length - index < token.length)
		return false;

	for(var i = index; i < string.length && i - index < token.length; i++) {

		if(string.charAt(i)!= token.charAt(i - index))
			return false;
	}

	return true;
}

module.exports = {

	tokenize,
	getToken,
	isToken
};

function add(parent) {

	let child = [];

	for(let i = 1; i < arguments.length; i++) {

		child = child.concat(
			Array.isArray(arguments[i]) ?
				arguments[i] :
				[arguments[i]]
		);
	}

	child.forEach((item) => {

		parent.children.push(item);

		item.parent = parent;
	});

	return parent;
}

function clean(element) {

	return {
		content: element.content != null ? element.content : "",
		children: element.children != null ?
			element.children.map((item) => {
				return clean(item);
			}) :
			[]
	};
}

function copy(element) {
	return setValues(JSON.parse(JSON.stringify(clean(element))));
}

function create(content, parent) {

	let children = [];

	for(let i = 2; i < arguments.length; i++) {

		children = children.concat(
			Array.isArray(arguments[i]) ?
				arguments[i] :
				[arguments[i]]
		);
	}

	return setValues({
		content: content != null ? content : "",
		parent: parent,
		children: children
	});
}

function get(element) {

	let path = [];

	for(let i = 1; i < arguments.length; i++) {

		path = path.concat(
			Array.isArray(arguments[i]) ?
				arguments[i] :
				[arguments[i]]
		);
	}

	for(let i = 0; i < path.length - 1; i++) {

		if(typeof path[i] == "string") {

			let match = element.children.filter((item) => {
				return item.content.toLowerCase() == path[i].toLowerCase();
			});

			if(match.length == 0)
				return [];

			element = match[0];
		}

		else if(path[i] >= 0 && path[i] < element.children.length)
			element = element.children[path[i]];

		else
			return [];
	}

	let id = path[path.length - 1];

	if(typeof id == "string") {

		return element.children.filter((item) => {
			return item.content.toLowerCase() == id.toLowerCase();
		});
	}

	else if(id >= 0 && id < element.children.length)
		return [element.children[id]];

	return [];
}

function getIndex(element) {

	if(element.parent == null)
		return -1;

	return element.parent.children.indexOf(element);
}

function getNest(element) {

	let nest = 0;
	let current = element;

	while(current.parent != null) {

		current = current.parent;

		nest++;
	}

	return nest;
}

function getPath(element) {

	let path = [];

	while(element.parent != null) {

		path.push(getIndex(element));

		element = element.parent;
	}

	return path;
}

function getRoot(element) {

	while(element.parent != null)
		element = element.parent;

