		for(let j = 0; j < tokens.length; j++) {

			if(!inComment && tokens[j] == "#")
				break;

			if(!inComment && tokens[j] == "#[")
				inComment = true;

			else if(inComment && tokens[j] == "]#")
				inComment = false;

			else if(!inComment)
