		let curlyStack = [];

		let root = one.create();
		let content = null;

		nest = 0;

		for(let j = 0; j < tokens.length; j++) {

			if(tokens[j].trim().length == 0 ||
				tokens[j] == "[>" || tokens[j] == "<]") {

				continue;
